import React, { useEffect, useState } from 'react'

import {Event} from "@material-ui/icons"


import {
    Alert,
    AlertIcon,
    AlertTitle,
    AlertDescription,
    CloseButton,
    useDisclosure,
  } from '@chakra-ui/react'
const StoreEvent = () => {

    const [error, setError] = useState({
        status: false,
        msg: "",
        type: "",
        desc:"",
      })


    const {
        isOpen: isVisible,
        onClose,
      } = useDisclosure({ defaultIsOpen: true })



      const handleSubmit=()=>{

      }

      useEffect(()=>{
        const inputs = document.querySelectorAll(".input");
    
    
        function addcl(){
            let parent = this.parentNode.parentNode;
            parent.classList.add("focus");
        }
        
        function remcl(){
            let parent = this.parentNode.parentNode;
            if(this.value === ""){
                parent.classList.remove("focus");
            }
        }
        
        
        inputs.forEach(input => {
            input.addEventListener("focus", addcl);
            input.addEventListener("blur", remcl);
        });
        
        
       })


  return (
   
    <>
    <div className='container'>
    <div className="login-content">
    <form onSubmit={handleSubmit} >
    
    <div className="input-div one">
        <div className='i'>
  
        
       
  
        </div>
        
        <div className='div'>
             <h5>Event Name</h5>
             <input className='input' type="text" name="event_name"  />
  
        </div>
    </div>
    <div className="input-div one">
        <div className='i'>
  
        
        
  
        </div>
        
        <div className='div'>
             <h5>Email</h5>
             <input className='input' type="email" name='email' />
  
        </div>
    </div>
  
               <div className="input-div pass">
                        <div className="i"> 
                            <Event/>
                        </div>
                        <div className="div">
                             <h5>Password</h5>
                             <input type="password" className="input" name='password'/>
                     </div>
                  </div>

                  <div className="input-div pass">
                        <div className="i"> 
                             
                        </div>
                        <div className="div">
                             <h5>Confirm Password</h5>
                             <input type="password" className="input" name='password_confirmation'/>
                     </div>
                  </div>
  
  
                 
                  <input type="submit" class="btn" value="Login"/>
  
    
    </form>


  
  
    </div>
    {error.status ?
    <div>
           <Alert status={error.type}>
  <AlertIcon />
  <AlertTitle>{error.msg}</AlertTitle>
  <AlertDescription>{error.desc}</AlertDescription>
  <CloseButton
        alignSelf='flex-start'
        position='relative'
        right={-1}
        top={-1}
        onClick={onClose}
      />
</Alert>
    </div>
    : ''}
  
  
    </div>
    </>
  )
}

export default StoreEvent
