import { Button } from '@chakra-ui/react'
import React, { Fragment, useEffect, useState } from 'react'

import { Spinner } from '@chakra-ui/react';

import { useGetLoggedUserQuery,useGetAllEventsQuery} from "../../sevices/userAuthApi";

import {getToken} from "../../sevices/LocalStorageServices";

const Home = () => {

  const token = getToken('token');


  const [userData,setUserData] = useState({
    email:"",
    name:""
  })

  let events = [];

  const {data ,isFetching,isSuccess} = useGetLoggedUserQuery(token);

  const {data:data1,isSuccess:isSuccess1} = useGetAllEventsQuery(token);



  useEffect(()=>{
    if(data && isSuccess){

      setUserData({
        email:data.user.email,
        name:data.user.name,

      })

    }

    if(data1 && isSuccess1){

      events=[];
      for(let i=0;i<data1.length;i++){
        events.push(data1[i]);
      }

    console.log(events);

    }
  },[data,isSuccess,isSuccess1,data1])

  
  return (

<Fragment>
  {isFetching ? (
    <Spinner
  thickness='4px'
  speed='0.65s'
  emptyColor='gray.200'
  color='blue.500'
  size='xl'
/>
  ):(

    <Fragment>

       <div> {userData.email} </div>

    </Fragment>
    

  )}
</Fragment>




   
  )
}

export default Home
