import React, { Fragment, useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';


import { useGetSingleEventsQuery} from "../../sevices/userAuthApi";


import { Spinner } from '@chakra-ui/react';

import Calendar from 'react-calendar';
import moment from 'moment';

import 'react-calendar/dist/Calendar.css';






const EvenDetails = () => {

    const {id} = useParams();



    const {data ,isFetching,isSuccess,isError} = useGetSingleEventsQuery(id);



    const mark = [
       new Date(4,4,2020)
    ]

    const day = {
        1:"moday",
        2:"tuesday",
        3:"wednesday",
        4:"thursday",
        5:"friday",
        6:"saturday",
        7:"sunday",

    }

    const month={
        1:31,
        3:31,
        4:30,
        5:31,
        6:30,
        7:31,
        8:31,
        9:30,
        10:31,
        11:30,
        12:31,
    }
    const [value, onChange] = useState(new Date());

    useEffect(()=>{

        if(isSuccess){

          // onChange(new Date(12,12,2021));
    
        }

    },[data,isSuccess])

   

    

    
    

  return (
    <Fragment>
        {isFetching ?
         (
            <Spinner
            thickness='4px'
            speed='0.65s'
            emptyColor='gray.200'
            color='blue.500'
            size='xl'
          />
         ):
        (<Fragment>

      <div className='calander-container'>
      <Calendar 


style={{ height: 500 }}
    onChange={onChange}
    value={value}
    tileClassName={({ date, view }) => {
      if(mark.find(x=>x===moment(date).format("DD-MM-YYYY"))){
       return  'highlight'
      }
    }}


    tileDisabled={({ date }) => date.getDay() === 0}

    /*maxDate={new Date(2020, 1, 0)}</div>*/
     minDate={
      new Date(3,2,2020)
    }

    
      
       >

       </Calendar>
      </div>


        </Fragment>)}
    </Fragment>
  )
}

export default EvenDetails
