import React, { Fragment } from 'react'
import { useParams } from 'react-router-dom';


import { useGetSingleEventsQuery} from "../../sevices/userAuthApi";

import {getToken} from "../../sevices/LocalStorageServices";

import { Spinner } from '@chakra-ui/react';






const EvenDetails = () => {

    const {id} = useParams();
    const token = getToken('token');

    const {data ,isFetching,isSuccess} = useGetSingleEventsQuery(id,token);




  return (
    <Fragment>
        {isFetching ?
         (
            <Spinner
            thickness='4px'
            speed='0.65s'
            emptyColor='gray.200'
            color='blue.500'
            size='xl'
          />
         ):
        (<div>{data.event_name}</div>)}
    </Fragment>
  )
}

export default EvenDetails
